  const headerStyle = {
    textAlign: "center",
    fontWeight: "bold",
  };

  const cellStyle = {
    textAlign: "center",
  };

  const tableStyle = {
    border: "1px solid black",
    width: "300px",
    margin: "20px auto",
  };
  return (
    <div>

        <table className="card">
          <thead className="card-head">
            <tr>
              <td colSpan={2}>{nama}</td>
            </tr>
          </thead>
          <tbody className="card-body">
            <tr>
              <td colSpan={2}>{member}</td>
            </tr>
            <tr>
              <td>
                <button onClick={message}>Message</button>
              </td>
              <td>
                <button>subcribe</button>
              </td>
            </tr>
          </tbody>
        </table>

    </div>
  );
};

export default User;
