import { Component } from "react";

// functionnya di sebut komponen
function App() {
  return (
    <div className="App">
      {/* cara manggil komponen ada 2 cara*/}
      {/* pertama */}
      <Header />
      {/* kedua */}
      {/* <Header></Header> */}
      <h1>Hello World</h1>
      {/* kalau value nya string */}
      <Car merk="Ferrari" harga={5}/>
      <Footer nama="Fajar Abdul Jabar" />
    </div>
  );
}

// nama komponen harus di awali dengan huruf kapital
const Header = () => {
  return (
    <nav>
      <ul>
        <li>Home</li>
        <li>About</li>
        <li>Login</li>
      </ul>
    </nav>
  );
};

const Car = ({merk, harga}) => {
  return (
    // tag kosong di sebut pragment
    <>
      <h3>Merk Mobil : {merk}</h3>
      <h4>Harga Mobil : {harga} Miliyar</h4>
      <h5>Keadaan Mobil : Baru</h5>
      <h5>Varian warna : Hitam, putih, Abu-Abu</h5>
      <button>Beli</button>
    </>
  );
};

class Footer extends Component {
  render() {
    return (
      <footer>
        <h3>Copyright &copy;2024 Developed by {this.props.nama}</h3>
        <span>Make with &#10084</span>
      </footer>
    );
  }
}

export default App;
