import React from "react";
import Movie from "../Movie/Movie.js";
import "./Movies.css";

const Movies = () => {
    const datas = [
        {
            title:"Judul Film 1",
            year:2022,
            genre:"Komedi",
            poster:"https://picsum.photos/200",
        },
        {
            title:"Judul Film 2",
            year:2022,
            genre:"Drama",
            poster:"https://picsum.photos/200",
        },
        {
            title:"Judul Film 3",
            year:2022,
            genre:"Horror",
            poster:"https://picsum.photos/200",
        },
        {
            title:"Judul Film 4",
            year:2022,
            genre:"Fantasy",
            poster:"https://picsum.photos/200",
        },
        {
            title:"Judul Film 5",
            year:2022,
            genre:"Drama",
            poster:"https://picsum.photos/200",
        },
        {
            title:"Judul Film ",
            year:2022,
            genre:"Drama",
            poster:"https://picsum.photos/200",
        },
        {
            title:"Judul Film 5",
            year:2022,
            genre:"Drama",
            poster:"https://picsum.photos/200",
        },
    ];
    return (
      <div className="movies-container">
        {
            datas.map((data) => {
                return (
                    <Movie
                    title={data.title}
                    year={data.year}
                    genre={data.genre}
                    poster={data.poster}
                  />
                )
            })
        }
      </div>
    );
  };

export default Movies;
