  },[nama])

  return (
    <div className="movies">
      <h1>{nama}'s Movies</h1>
      <button onClick={handleClick}>Add a Movie</button>
      <AddMovieForm />
      <Counter label="Total movies" />
      <hr />
      {/* map through the data array and create a new component for each object */}
      {datas.map((movie) => (
        <Movie key={movie.title} {...movie} />
      ))}
    </div>
  )
  const addMovie = (movie) => {
    setDatas([...datas,movie])
  }

  console.log(datas);

  return (
    <div>
      <h1>Latest Movies</h1>
      <div className="movies-container">
        {datas.map((data) => {
          return (
            <Movie
              title={data.title}
              year={data.year}
              poster={data.poster}
              genre={data.genre}
            />
          );
        })}
        {/* <button onClick={handleClick}>Add Movie</button> */}
        {/* <Counter/> */}
      </div>
      <AddMovieForm onAddMovie={addMovie}/>
    </div>
  );
};

export default Movies;
