import React, { useState } from "react";
import Movie from "../Movie/Movie.js";
import "./Movies.css";

const Movies = () => {

  const [datas, setDatas] = useState()



  const handleClick = () => {
    const movie = {
      title: "Amazing Spiderman",
      year: 2012,
    };
    datas.push(movie);
  };

  console.log(datas);

  return (
    <div>
      <h1>Latest Movies</h1>
      <div className="movies-container">
        {datas.map((data) => {
          return (
            <Movie
              title={data.title}
              year={data.year}
              poster={data.poster}
              genre={data.genre}
            />
          );
        })}
        <button onClick={handleClick}>Add Movie</button>
      </div>
    </div>
  );
};

export default Movies;
