import "./Movies.css";
import Counter from "../Counter/Counter.js";
import AddMovieForm from "../AddMovieForm/AddMovieForm.js";
import { getMovieList } from "../../Api.js";

const Movies = () => {
  const [nama, setNama] = useState("Ucup");
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    getMovieList().then((result) => {
      setMovies(result);
    });
  }, []);

  const addMovie = (movie) => {
    setMovies([...movies, movie]);
  };

  return (
    <div className="movies">
      <h1>{nama}'s Movies</h1>
      <button onClick={() => addMovie({ title: "Amazing Spiderman", year: 2012, poster: "https://picsum.photos/200", genre: "Avanger" })}>
        Add a Movie
      </button>
      <AddMovieForm onAddMovie={addMovie} />
      <Counter label="Total movies" />
      <hr />
      {movies.map((movie) => (
        <Movie key={movie.title} {...movie} />
      ))}
    </div>
  );
};

export default Movies;
