import { Component } from "react";
import Header from "./component/Header.js";


// functionnya di sebut komponen
function App() {
  return (
    <div className="App">
      {/* cara manggil komponen ada 2 cara*/}
      {/* pertama */}
      <Header />
      {/* kedua */}
      {/* <Header></Header> */}
      <h1>Hello World</h1>
      {/* kalau tipe datanya nya string maka gunakan tanda kutif,
      jika tipe datanya integer maka gunakan kurung kurawal,
      kalau tipe datanya Boolean maka gunakan isNew kurung kurawal, 
      jika tipe datanya array maka gunakan kurung kurawal,
      kalau berupa function maka gunakan callback function
      */}

      <Footer nama="Fajar Abdul Jabar" />
      <User
        nama="Ucup"
        alamat="Jakarta"
        angkatan={11}
        hobi={["Bernang", "Bermain Game", "Membaca Buku"]}
        status={false}
        button={() => alert("Profil  telah dibuka")}
      />
       <User
        nama="Rafi"
        alamat="Tanggerang"
        angkatan={10}
        hobi={["Bernang", "Bermain Game", "Membaca Buku"]}
        status={false}
        button={() => alert("Profil  telah dibuka")}
      />
      <User
        nama="Galih"
        alamat="Bandung"
        angkatan={9}
        hobi={["Bernang", "Bermain Game", "Membaca Buku"]}
        status={false}
        button={() => alert("Profil  telah dibuka")}
      />
    </div>
  );
}



const Car = (props) => {
  const { merk, harga, isNew, colors, beli } = props;
  return (
    // tag kosong di sebut pragment
    <>
      <h3>Merk Mobil : {merk}</h3>
      <h4>Harga Mobil : {harga} Miliyar</h4>
      {/* kalau Boolean maka harus pake ternery */}
      <h5>Keadaan Mobil : {isNew ? "Baru" : "Bekas"}</h5>
      {/* kalau tipe datany array maka pake s 
      map untuk menambahkan spasi */}
      <h5>Varian warna : {colors.map((color) => color + " ,")}</h5>
      <button onClick={beli}>Beli</button>
    </>
  );
};

const User = (props) => {
  const { nama, alamat, angkatan, hobi, status, button } = props;
  return (
    <div>
      <ul>
        <li>Nama : {nama}</li>
        <li>Alamat : {alamat}</li>
        <li>Angkatan : ke-{angkatan}</li>
        <li>Hobi : {hobi.map((hobi) => hobi + ", ")}</li>
        <li>Status : {status ? "lulus" : "sedang belajar"}</li>
        <button onClick={button}>Message</button>
      </ul>
    </div>
  );
};

class Footer extends Component {
  render() {
    return (
      <footer>
        <h3>Copyright &copy;2024 Developed by {this.props.nama}</h3>
        <span>Make with &#10084</span>
      </footer>
    );
  }
}

export default App;
