import React from "react";
import { useParams } from "react-router-dom";
import "./Detail.css"; // Import CSS file for styling

const Detail = () => {
    let { id } = useParams();

    // Dummy data for demonstration
    const movie = {
        title: "Cool Movie",
        poster: "https://example.com/poster.jpg",
        overview: "This is a really cool movie. You should definitely watch it!",
    };

    return (
        <div className="detail-container">
            <h1 className="cool-title">{movie.title}</h1>
            <div className="poster-container">
                <img src={movie.poster} alt="Movie Poster" className="poster" />
            </div>
            <div className="overview-container">
                <p className="overview">{movie.overview}</p>
            </div>
            <p>ID: {id}</p>
        </div>
    );
};

export default Detail;
